package main
import (
  "encoding/json"
  "fmt"
)

type Ticket struct {
  Subject string
}

func main() {
  jsonString := "{ \"subject\": \"Travis Subject\" }"
  var jsonData = []byte(jsonString)
  var myTicket Ticket
  
  err := json.Unmarshal(jsonData, &myTicket)

  if err != nil {
    fmt.Println(err)
  }
  
  fmt.Println(myTicket.Subject)
}